package com.example.gravity.devxplore.screens.base.Explore;import android.content.Context;import android.support.annotation.NonNull;import android.support.annotation.Nullable;import com.example.gravity.devxplore.screens.base.BasePresenter;import com.example.gravity.devxplore.screens.base.BaseView;import com.example.gravity.devxplore.data.model.User;import java.util.List;/** * Created by gravity on 7/29/17. */public interface ExploreContract {    interface XploreView extends BaseView<Presenter> {        void showLoadingIndicator(boolean isLoading);        void showUsers(List<User> users);    }    interface Presenter extends BasePresenter {        void loadUsers(@NonNull String location, @NonNull String language);        void openUserDetails(Context context, @NonNull String username);        @Nullable        String getLanguage();        void setLanguage(String mLanguage);        @Nullable        String getLocation();        void setLocation(String mLocation);        void updateFavourite(User user, boolean isFavourite);    }}